console.log('This is your todo list!!');
document.addEventListener('DOMContentLoaded', function () {
    var message = document.createElement('p');
    message.textContent = 'Welcome to your todo list!!';
    document.body.appendChild(message);
});
document.addEventListener('DOMContentLoaded', function () {
    var input = document.createElement('input');
    input.type = 'text';
    input.placeholder = 'Type a thing to do!!';
    document.body.appendChild(input);
    //Allow users to put the number of times they want ot complete the task
    var times = document.createElement('input');
    times.type = 'number';
    times.placeholder = '1';
    document.body.appendChild(times);
    var button = document.createElement('button');
    button.textContent = 'Click to Add';
    document.body.appendChild(button);
    var list = document.createElement('ul');
    document.body.appendChild(list);
    button.addEventListener('click', function () {
        if (typeof input.value === 'string') {
            var todolist_1 = document.createElement('li');
            todolist_1.textContent = input.value;
            list.appendChild(todolist_1);
            //atach times to the todo lists
            var ftimes_1 = document.createElement('span');
            ftimes_1.textContent = times.value;
            todolist_1.appendChild(ftimes_1);
            //empty the list
            input.value = '';
            var xbutton = document.createElement('button');
            xbutton.textContent = 'finished';
            todolist_1.appendChild(xbutton);
            xbutton.addEventListener('click', function () {
                var timesint = parseInt(ftimes_1.textContent || '0', 10) - 1;
                ftimes_1.textContent = timesint.toString();
                if (timesint <= 0) {
                    list.removeChild(todolist_1);
                }
            });
        }
    });
});
